import { ADD_EXPENSE } from "../action-types/expenses";

const initialState = {
  expensesList: [],
};

export const expenseReducer = (state = initialState, action) => {
  switch (action.type) {
    case ADD_EXPENSE: {
      return {
        ...state,
        expensesList: [...state.expensesList, data],
      };
    }
    default:
      return state;
  }
};
